'use strict';

var DataAccessMessage = require('./lib/messages/DataAccessMessage');
var SecurityMessage = require('./lib/messages/SecurityMessage');
var nodeUtil = require('util');
var utils = require('./lib/utils');


exports.create = function (objectID, objectName) {
  return new DataAccessMessage('create', objectID, objectName);
};

exports.read = function (objectID, objectName) {
  return new DataAccessMessage('read', objectID, objectName);
};

exports.update = function (objectID, objectName) {
  return new DataAccessMessage('update', objectID, objectName);
};

exports.delete = function (objectID, objectName) {
  return new DataAccessMessage('delete', objectID, objectName);
};

exports.securityMessage = function () {
  utils.validate.isProvided(arguments[0], 'An argument');
  var message = nodeUtil.format.apply(null, arguments);
  return new SecurityMessage(message);
};
