'use strict';

var MessageBase = require('./MessageBase');

module.exports = SecurityMessage;

function SecurityMessage(message) {
  MessageBase.call(this, null);
  this._custom.message = message;
}

require('util').inherits(SecurityMessage, MessageBase);


SecurityMessage.prototype.toString = function () {
  var parts = this._stringifyParts();
  return parts.verb + ',' + parts.custom;
};

SecurityMessage.prototype.log = function (callback) {
  this._outputToTransport(callback);
};
